(function(){tinymce.create("tinymce.plugins.XHTMLXtrasPlugin",{init:function(A,C){A.addCommand("mceCite",function(){A.windowManager.open({file:C+"/cite.htm",width:350+parseInt(A.getLang("xhtmlxtras.cite_delta_width",0)),height:250+parseInt(A.getLang("xhtmlxtras.cite_delta_height",0)),inline:1},{plugin_url:C})
});
A.addCommand("mceAcronym",function(){A.windowManager.open({file:C+"/acronym.htm",width:350+parseInt(A.getLang("xhtmlxtras.acronym_delta_width",0)),height:250+parseInt(A.getLang("xhtmlxtras.acronym_delta_width",0)),inline:1},{plugin_url:C})
});
A.addCommand("mceAbbr",function(){A.windowManager.open({file:C+"/abbr.htm",width:350+parseInt(A.getLang("xhtmlxtras.abbr_delta_width",0)),height:250+parseInt(A.getLang("xhtmlxtras.abbr_delta_width",0)),inline:1},{plugin_url:C})
});
A.addCommand("mceDel",function(){A.windowManager.open({file:C+"/del.htm",width:340+parseInt(A.getLang("xhtmlxtras.del_delta_width",0)),height:310+parseInt(A.getLang("xhtmlxtras.del_delta_width",0)),inline:1},{plugin_url:C})
});
A.addCommand("mceIns",function(){A.windowManager.open({file:C+"/ins.htm",width:340+parseInt(A.getLang("xhtmlxtras.ins_delta_width",0)),height:310+parseInt(A.getLang("xhtmlxtras.ins_delta_width",0)),inline:1},{plugin_url:C})
});
A.addCommand("mceAttributes",function(){A.windowManager.open({file:C+"/attributes.htm",width:380,height:370,inline:1},{plugin_url:C})
});
A.addButton("cite",{title:"xhtmlxtras.cite_desc",cmd:"mceCite"});
A.addButton("acronym",{title:"xhtmlxtras.acronym_desc",cmd:"mceAcronym"});
A.addButton("abbr",{title:"xhtmlxtras.abbr_desc",cmd:"mceAbbr"});
A.addButton("del",{title:"xhtmlxtras.del_desc",cmd:"mceDel"});
A.addButton("ins",{title:"xhtmlxtras.ins_desc",cmd:"mceIns"});
A.addButton("attribs",{title:"xhtmlxtras.attribs_desc",cmd:"mceAttributes"});
if(tinymce.isIE){function B(E,D){if(D.set){D.content=D.content.replace(/<abbr([^>]+)>/gi,"<html:abbr $1>");
D.content=D.content.replace(/<\/abbr>/gi,"</html:abbr>")
}}A.onBeforeSetContent.add(B);
A.onPostProcess.add(B)
}A.onNodeChange.add(function(F,G,D,E){D=F.dom.getParent(D,"CITE,ACRONYM,ABBR,DEL,INS");
G.setDisabled("cite",E);
G.setDisabled("acronym",E);
G.setDisabled("abbr",E);
G.setDisabled("del",E);
G.setDisabled("ins",E);
G.setDisabled("attribs",D&&D.nodeName=="BODY");
G.setActive("cite",0);
G.setActive("acronym",0);
G.setActive("abbr",0);
G.setActive("del",0);
G.setActive("ins",0);
if(D){do{G.setDisabled(D.nodeName.toLowerCase(),0);
G.setActive(D.nodeName.toLowerCase(),1)
}while(D=D.parentNode)
}})
},getInfo:function(){return{longname:"XHTML Xtras Plugin",author:"Moxiecode Systems AB",authorurl:"http://tinymce.moxiecode.com",infourl:"http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/xhtmlxtras",version:tinymce.majorVersion+"."+tinymce.minorVersion}
}});
tinymce.PluginManager.add("xhtmlxtras",tinymce.plugins.XHTMLXtrasPlugin)
})();